#!/usr/bin/env python3
# -*- coding:utf-8 -*-

import base64, struct

bmp_data = base64.b64decode('')


def bmp_info(data):
	if len(data) < 30:
		print('This is not a bitmap!')
	else:
		info = struct.unpack('<ccIIIIIIHH', data[:30])
		if info[0] == b'B' and (info[1] == b'M' or info[2] == b'A'):
			return {
				'width': info[6],
				'height': info[7],
				'color': info[9]
			}
# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')
print(bi)